// Place global declarations here.
        int psi;
         chan reset;
         chan resetExp;
         chan inspirar, espirar;
         chan endSetup;
         chan expOn, expOff;
        int Q; // Int representation of q
        /**sefsefse
        Tendremos dos inyectores, uno de aire y uno de oxigeno
        Cada inyector tiene un dato tipo bool que indica si está encendido o no: isOn
        Además un entero que indica el caudal que circulará

        **/
        typedef struct {
        bool isOn;
        int caudal;
        } Inyector;

// Factor de escalad 10E4
const double PI = 3.141592;
const double d1 = 0.0254; // m
const double l1 = 3.0; // longitud mangera 1 m
const double l2 = 0.3; // longitud mangera 2 m
const double dt = 0.01; // s
const double w = 1.81E-5; // Ns / m^2

double q;
int v1 = 4250; // volumen del tanque 1 m^3
int v2 = 1000;    // volumen del tanque 2 m^3
int p1 = 10000; // Presion inicial del tanque 1 Psi
int p2 = 100; // Presion inicial del tanque 2 Psi
int correct;
double delta, delta2;

double p2x = 6894.76;
double p1x = 68947.6;

int getQ(int p_i, int p_j, double l) {
    double foo = (p_i*6894.76)/100;
    double bar = (p_j*6894.76)/100;
    double div = l*128*w;
    if (div == 0) {
        div = 1;
    }
    return fint((PI * (foo-bar) * pow(d1,4)) / div);
}

int getDelta() {
//    p2x = p2x + (dt*q)/(1E-3);
//    delta = (100*dt*Q)/(1E-3);
//    delta2 = (dt*q)/(1E-3);
    return fint((100*dt*Q)/(6894.76*1E-3));
}

template.declaration:
// Place local declarations here. /** Configuración de ciclos de trabajo de los inyectores flowrate: Es el caudal requerido ([L^3 T^-1]; dado en litros por minuto ) FiO2: Fracción de oxígeno inspirado, dado en porcentaje de oxigeno en el aire **/ int[0,500] flowrate; int[0,500] o2WithoutAir;
/** De acuerdo a la siguiente tabla
| x | y | ------------ | 120 | 30 | | 220 | 40 | | 280 | 50 | | 350 | 60 | | 380 | 70 | | 410 | 80 | | 450 | 90 | | 500 | 100 |
Se construye una función para extrapolar los valores, dicha función es de grado 2 debido al desbordamiento de int teniendo f(x) = 0,0001x^2+0,1441x+3,6656 **/
int getDuty(int x){ return (x<=120)? 30: (x<=220)? 40: (x<=280)? 50: (x<=350)? 60: (x<=380)? 70: (x<=410)? 80: (x<=450)? 90: 100; //more than 450, expected until 500
}

clock time;
/** De acuerdo a la siguiente tabla
| apertura | caudal | --------------------- | 30 | 120 | | 40 | 220 | | 50 | 280 | | 60 | 350 | | 70 | 380 | | 80 | 410 | | 90 | 450 | | 100 | 500 |
Adicionales para completar las posibilidades con el duty de 30% | apertura | caudal | --------------------- | 00 | 120 | | 10 | 120 | | 20 | 120 |
**/ int params[11] = {120,120,120,120,220,280,350,380,410,450,500};
int getFlowWithPassage(int n){ return params[n/10]; }

nta.system:
// Place template instantiations here.

        /**
        Configuración de ciclos de trabajo de los inyectores
