nta.declaration:
/**
Input params:
Inspiration time (tInsp) cs
Expiration time  (tExp)  cs
Inspiration pause (pInsp)cs
Expiration pause  (pExp) cs
Flowrate 
FiO2
*/
const int tInsp=100;
const int tExp=300;
const int pInsp=0;
const int pExp=30;
const int totalT = tInsp + tExp;


int psi;
chan reset;
chan resetExp;
chan inspiration, expiration;
chan endSetup;
chan expOn, expOff;
int Q; // Int representation of q
/**
Two injectors, one for oxygen and one for air
**/
typedef struct {
    bool isOn;
    int flowrate;
} Injector;

// With scaling factor 10E4
const double PI = 3.141592;
const double d1 = 0.0254; // (m)
const double l1 = 3.0; // hose lenght 1 (m)
const double l2 = 0.3; // hose lenght 2 (m)
const double dt = 0.01; // (s)
const double w = 1.81E-5; // (Ns / m^2)

double q;
int v1 = 4250; // tank volume 1 (m^3)
int v2 = 1000;    // tank volume 2 (m^3)
int p1 = 10000; // initial pressure in tank 1 (Psi)
int p2 = 100; // initial pressure in tank 2 (Psi)
int correct;
double delta, delta2;

double p2x = 6894.76;
double p1x = 68947.6;

int getQ(int p_i, int p_j, double l) {
    double foo = (p_i*6894.76)/100;
    double bar = (p_j*6894.76)/100;
    double div = l*128*w;
    if (div == 0) {
        div = 1;
    }
    return fint((PI * (foo-bar) * pow(d1,4)) / div);
}

int getDelta() {
//    p2x = p2x + (dt*q)/(1E-3);
//    delta = (100*dt*Q)/(1E-3);
//    delta2 = (dt*q)/(1E-3);
    return fint((100*dt*Q)/(6894.76*1E-3));
}

template.declaration:
/** Injector workcycle configuration flowrate: Required flow ([L^3 T^-1]; given in liters per minute ) FiO2: Fraction of inspirated oxygen, given in percentage of oxygen in the air Duty: The opening of the injector's valve, where 100 is fully opened and 0 is fully closed. **/ int[0,500] flowrate; int[0,500] o2WithoutAir;
/**
The following table matches the duty for a given flowrate. e.g. if a flowrate of 220 is required, the injectors should open 40%. This data was provided by profs. José I. García and Andrés M. Valencia. It was acquired experimentally and it is specific to injectors of the the mechanical ventilator featured in the article.
|flowrate|duty (%) | -------------------- | 120 | 30 | | 220 | 40 | | 280 | 50 | | 350 | 60 | | 380 | 70 | | 410 | 80 | | 450 | 90 | | 500 | 100 |
These points could be extrapolated with a 2nd grade polynomial f(x) = 0,0001x^2+0,1441x+3,6656 **/
int getDuty(int flow){ return (flow <= 120)? 30: (flow <= 220)? 40: (flow <= 280)? 50: (flow <= 350)? 60: (flow <= 380)? 70: (flow <= 410)? 80: (flow <= 450)? 90: 100; //more than 450, expected until 500 }

clock time;
/** Using the inverse of the flowrate vs. duty table
| duty | flow | --------------------- | 30 | 120 | | 40 | 220 | | 50 | 280 | | 60 | 350 | | 70 | 380 | | 80 | 410 | | 90 | 450 | | 100 | 500 |
and by completing low values with the extrapolated function
| duty | flow | --------------------- | 00 | 0 | | 10 | 50 | | 20 | 100 |
**/ int params[11] = {0,50,100,120,220,280,350,380,410,450,500};
int getFlowWithPassage(int n){ return params[n/10]; }

nta.system:
/**
Injector workcycle configuration
flowrate: Required flow ([L^3 T^-1]; given in liters per minute )
FiO2: Fraction of inspirated oxygen, given in percentage of oxygen in the air
**/
int[0,500] flowrate = 500;
int[1,10] FiO2 = 10;

/**
Duty: The opening of the injector's valve, where 100 is fully opened and 0 is fully closed.
**/
int[0,100] airDuty;
int[0,100] o2Duty;

/**
channel to notify when injectors are configured and ready
**/
chan configDone;

//flowrate, FiO2, inputs
//airDuty, o2Duty, outputs
config = Setup(flowrate, FiO2, airDuty, o2Duty);

chan turnO2On; 
chan turnAirOn;
chan turnO2Off;
chan turnAirOff;

int mix=0;

chan O2Mixed;
chan airMixed;

o2Valve = MixChamber(O2Mixed, turnO2On, turnO2Off, o2Duty, configDone, mix);
airValve = MixChamber(airMixed, turnAirOn, turnAirOff, airDuty, configDone, mix);

/**
expiration valve
**/
broadcast chan mixExpired;
chan turnExpValveOn;
chan turnExpValveOff;

expValve = ExpValve(mixExpired, mix);

Ventilator = Control(pInsp,pExp,tInsp,tExp, totalT, O2Mixed, turnO2On, turnO2Off, airMixed, turnAirOn, turnAirOff, mixExpired);

system  config, o2Valve, airValve, expValve, Ventilator;
